// â”€â”€ ë²ˆì—­ ì ìš© í•¨ìˆ˜ â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function applyI18n(lang) {
    const t = i18nData[lang];
    if (!t) return;

    // textContent êµì²´
    document.querySelectorAll('[data-i18n]').forEach(el => {
        const key = el.dataset.i18n;
        if (t[key] !== undefined) el.textContent = t[key];
    });

    // innerHTML êµì²´ (HTML íƒœê·¸ í¬í•¨)
    document.querySelectorAll('[data-i18n-html]').forEach(el => {
        const key = el.dataset.i18nHtml;
        if (t[key] !== undefined) el.innerHTML = t[key];
    });

    // placeholder êµì²´
    document.querySelectorAll('[data-i18n-ph]').forEach(el => {
        const key = el.dataset.i18nPh;
        if (t[key] !== undefined) el.placeholder = t[key];
    });

    // aria-label êµì²´
    document.querySelectorAll('[data-i18n-aria]').forEach(el => {
        const key = el.dataset.i18nAria;
        if (t[key] !== undefined) el.setAttribute('aria-label', t[key]);
    });

    document.documentElement.lang = lang;

    // ì–¸ì–´ ë²„íŠ¼ active ìƒíƒœ
    document.querySelectorAll('.lang-btn').forEach(btn => {
        btn.classList.toggle('active', btn.dataset.lang === lang);
    });
}

// â”€â”€ í˜ì´ì§€ ë¡œë“œ ì‹œ ìë™ ì ìš© â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
(function () {
    const lang = localStorage.getItem('lang') || 'ko';
    // DOM ì¤€ë¹„ í›„ ì‹¤í–‰
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', () => applyI18n(lang));
    } else {
        applyI18n(lang);
    }
})();
